sys_script_client.script:
function onLoad() {
    // Check that this is a version and not the published article
    if (g_scratchpad.canWrite == "false") {
        return;
    }
    var scripts = [
        "x_58056_md_docs.easymde.min.jsdbx",
        "x_58056_md_docs.codemirror.jsdbx",
        "x_58056_md_docs.initialize_easymde.jsdbx",
        "x_58056_md_docs.bootstrap.min.jsdbx",
        "x_58056_md_docs.html2canvas.jsdbx",
        "x_58056_md_docs.jspdf.debug.jsdbx",
        //"x_58056_md_docs.jspdf.min.jsdbx",
        "x_58056_md_docs.codemirror_autorefresh.jsdbx"
    ];

    g_form.setDisplay("api_markdown", false);

    ScriptLoader.getScripts(scripts, afterLoad);

    function afterLoad() {
        //markdownDocument.createEasyMDE(g_form.getControl("api_markdown").id, g_form.getValue("api_markdown"));
        var hdrMDE = markdownDocument.createEasyMDE("markdown-document-textarea", g_form.getValue("api_markdown"), g_form.getUniqueValue());
        if (!hdrMDE) {
            alert("ERROR Markdown Document controller not created");
            return;
        }
        hdrMDE.codemirror.refresh();
        hdrMDE.codemirror.setOption("autoRefresh", true);

		// Update the HTML if we have markdown text but no html yet
        if (g_form.getValue("html") == "" && g_form.getValue("api_markdown") != "") {
            updateHTML(hdrMDE);
        }
    }
}

function updateHTML(mde) {
    var mdText = mde.value();
    var sourceHTML = mde.options.previewRender(mdText);

    var styleSheetIds = g_scratchpad.style_sheet_ids.split(",");
    var cssHdrText = '<header>';
    styleSheetIds.forEach(function(id) {
        cssHdrText += '\n<link href="' + id + '.cssdbx" rel="stylesheet" type="text/css"></link>';
    }, this);
    cssHdrText += '\n</header>';

    g_form.setValue("html", cssHdrText + "\n<div class='content'>\n" + sourceHTML + "\n</div>");
}
