sys_script_client.script:
function onSubmit() {
	if (typeof markdownDocument == "undefined") {
		return;
	}
	var hdrMDE = markdownDocument.getEasyMDE(g_form.getControl("api_markdown").id);
	var sourceHTML = hdrMDE.options.previewRender(hdrMDE.value());

	var styleSheetIds = g_scratchpad.style_sheet_ids.split(",");
	var cssHdrText = '<header>';
	styleSheetIds.forEach(function(id) {
		cssHdrText += '<link href="' + id + '.cssdbx" rel="stylesheet" type="text/css"></link>';
	}, this);
	cssHdrText += '</header>';

	g_form.setValue("html", cssHdrText + "\n<div class='content'>\n" + sourceHTML + "\n</div>");

	hdrMDE.toTextArea();
	markdownDocument = null;
}
