sys_script_include.script:
/**
 * @class MarkdownSourceTables
 */

MarkdownSourceTables = Class.create();

MarkdownSourceTables.prototype = {
    /**
     * Initialize variables
     */
    initialize: function() {},

    /**
     * Returns a list of tables valid for source
     */
    process: function() {
        var tables = [];
        var srcTblGR = new GlideRecord("x_58056_md_docs_source_tables");
        srcTblGR.addQuery("active", true);
        srcTblGR.query();
        while (srcTblGR.next()) {
            tables.push(srcTblGR.table.toString());
        }
        return tables;
    },

    type: "MarkdownSourceTables"
};
