sys_ui_action.script:
function createPDFAttachment() {
    var html;
    if (!markdownDocument) {
        html = g_form.getValue("html");
    } else {
        var hdrMDE = markdownDocument.getEasyMDE(g_form.getUniqueValue());
        html = hdrMDE.options.previewRender(hdrMDE.value());
    }

    var ga = new GlideAjax("x_58056_md_docs.MarkdownAJAX"); //Call script include to escape text
    ga.addParam("sysparm_name", "getHTML");
    ga.addParam("sysparm_table", g_form.getTableName());
    ga.addParam("sysparm_sys_id", g_form.getUniqueValue());
    ga.addParam("sysparm_content", html);
    ga.getXMLAnswer(_createPDFAttachment);
}

function _createPDFAttachment(answer) {
    var pdf = new jsPDF();

    var specialElementHandlers = {
        '#editor': function(element, renderer) {
            return true;
        }
    };

    pdf.fromHTML(answer, 15, 15, {
        width: 180,
        elementHandlers: specialElementHandlers,
    });

    // pdf.save(g_form.getValue("source_name").replace(".", "-") + ".pdf");

            var ga = new GlideAjax("x_58056_md_docs.MarkdownAJAX"); //Call script include to escape text
            ga.addParam("sysparm_name", "createPDFFile");
            ga.addParam("sysparm_table", g_form.getTableName());
            ga.addParam("sysparm_sys_id", g_form.getUniqueValue());
            ga.addParam("sysparm_content", pdf.output());
            ga.getXMLAnswer(checkPDFFileResults);
}

function checkPDFFileResults(answer) {
    if (answer != "ok") {
        alert(answer);
    } else {
        var list = GlideList2.get("x_58056_md_docs_markdown_source.REL:b9edf0ca0a0a0b010035de2d6b579a03");
        list.refresh();
    }
}
