sys_ui_action.script:
function getMarkdownText() {
    var sourceType = g_form.getValue("source_type");
    var sourceID = g_form.getValue("source_id");
    var remoteSourceID = g_form.getValue("remote_source_id");
    var sourceTable = g_form.getValue("source_table");
    var sourceScriptField = g_form.getValue("source_script_field");
    var sourceRemote = g_form.getValue("remote");
    var sourceName = g_form.getValue("source_name");
    var sourceDescription = g_form.getValue("source_description");
    var sourceText = g_form.getValue("source_text");

    var fieldsMissing = false;
    if (sourceType == "") {
        fieldsMissing = true;
    } else if (sourceType == "Document") {
        if (sourceRemote == "false") {
            if (sourceID == "" || sourceTable == "" || sourceScriptField == "") {
                fieldsMissing = true;
            }
        } else {
            if (remoteSourceID == "" || sourceTable == "" || sourceScriptField == "") {
                fieldsMissing = true;
            }
        }
    } else if (sourceName == "" || sourceText == "") {
        fieldsMissing = true;
    }
    if (fieldsMissing) {
        alert("Please complete required fields before clicking Refresh Markdown");
        return;
    }

    var ga = new GlideAjax('x_58056_md_docs.MarkdownAJAX');
    ga.addParam('sysparm_name', 'getSourceMarkdownText');
    ga.addParam('sysparm_source_type', sourceType);
    ga.addParam('sysparm_source_table', sourceTable);
    ga.addParam('sysparm_source_id', sourceID);
    ga.addParam('sysparm_remote_source_id', remoteSourceID);
    ga.addParam('sysparm_source_script_field', sourceScriptField);
    ga.addParam('sysparm_remote', sourceRemote);
    ga.addParam('sysparm_source_name', sourceName);
    ga.addParam('sysparm_source_description', sourceDescription);
    ga.addParam('sysparm_source_text', sourceText);
    ga.getXMLAnswer(checkResults);

}

function checkResults(answer) {
    var result = JSON.parse(answer);
    if (!result.ok) {
        alert(result.msg);
    } else {
        var hdrMDE = markdownDocument.getEasyMDE(g_form.getUniqueValue());
        if (!hdrMDE) {
            alert("ERROR Markdown Document controller not created");
            return;
        }
        hdrMDE.toTextArea();
        g_form.setSectionDisplay("markdown", true);
        g_form.setSectionDisplay("html", true);
        g_tabs2Sections.setActive(1);
        g_form.setValue("api_markdown", result.md);
        hdrMDE = markdownDocument.createEasyMDE("markdown-document-textarea", g_form.getValue("api_markdown"), g_form.getUniqueValue());
        hdrMDE.codemirror.refresh();
        hdrMDE.codemirror.setOption("autoRefresh", true);
        jQuery("#show_markdown_div").hide();
        jQuery("#hide_markdown_div").show();
    }
}
