sys_ui_script.script:
(function(global, factory) {
    global.markdownDocument = factory(global.jQuery);
})(this, function(jq) {
    MarkdownDocument = function() {
        var MyEasyMDE = {};
        var MD = this;

        MD.createEasyMDE = function(taName, initialText) {
            taName = taName.replace(/(:|\.|\[|\]|,|=)/g, "\\$1");
            var jqe = jq("#" + taName)[0];
            MyEasyMDE[taName] = new EasyMDE({
                element: jqe,
                spellChecker: false,
                autosave: {
                    enabled: true,
                    uniqueId: taName,
                    delay: 1000
                },
                placeholder: "Type your markdown here...",
                //forceSync: true,
                initialValue: initialText
            });
            return MyEasyMDE[taName];
        };

        MD.getEasyMDE = function(taName) {
            taName = taName.replace(/(:|\.|\[|\]|,|=)/g, "\\$1");
            return MyEasyMDE[taName];
        };
    };

    return new MarkdownDocument(); 
});
