                        (K.prototype.createStatusbar = function(e) {
                            e = e || this.options.status;
                            var t = this.options,
                                n = this.codemirror;
                            if (e && 0 !== e.length) {
                                var r,
                                    i,
                                    o,
                                    a = [];
