                                                  e.innerHTML = q(n.getValue());
                                              }))
                                            : "lines" === l
                                            ? ((o = function(e) {
                                                  e.innerHTML = n.lineCount();
                                              }),
                                              (i = function(e) {
                                                  e.innerHTML = n.lineCount();
                                              }))
                                            : "cursor" === l
                                            ? ((o = function(e) {
                                                  e.innerHTML = "0:0";
                                              }),
                                              (i = function(e) {
