sys_ui_script.script:
(function(global, factory) {
    global.markdownDocument = factory(global.jQuery);
})(this, function(jq) {

    MarkdownDocument = function() {
        var MySimpleMDE = {};
		var MD = this;

        MD.createSimpleMDE = function(taName, initialText) {
            taName = taName.replace(/(:|\.|\[|\]|,|=)/g, "\\$1");
            var jqe = jq("#" + taName)[0];
            MySimpleMDE[taName] = new SimpleMDE({
                element: jqe,
                spellChecker: false,
                autosave: {
                    enabled: true,
                    uniqueId: taName,
                    delay: 1000,
                },
                placeholder : "Type your markdown here...",
                //forceSync: true,
                initialValue: initialText
            });
            return MySimpleMDE[taName];
        };

        MD.getSimpleMDE = function(taName) {
            taName = taName.replace(/(:|\.|\[|\]|,|=)/g, "\\$1");
            return MySimpleMDE[taName];
        };
    };

    return new MarkdownDocument();
});
